DROP TABLE IF EXISTS `art_log_events_t`;

CREATE TABLE `art_log_events_t` ( id VARCHAR(64) NOT NULL, eventDate DATETIME DEFAULT NULL, alarmId VARCHAR(15) DEFAULT NULL, customerId VARCHAR(64) DEFAULT NULL, userId VARCHAR(64) DEFAULT NULL, userIpAddress VARCHAR(20) DEFAULT NULL, userPort INT(10) DEFAULT NULL, geoLatitude DOUBLE DEFAULT NULL, geoLongitude DOUBLE DEFAULT NULL, sessionId VARCHAR(64) DEFAULT NULL, requestId VARCHAR(64) DEFAULT NULL, boundedContextId VARCHAR(5) DEFAULT NULL, domainId VARCHAR(5) DEFAULT NULL, className VARCHAR(500) DEFAULT NULL, methodName VARCHAR(200) DEFAULT NULL, message VARCHAR(4000) DEFAULT NULL, exception VARCHAR(2000) DEFAULT NULL, context INT(5) DEFAULT NULL, severity INT(5) DEFAULT NULL, layer INT(5) DEFAULT NULL, eventAction INT(5) DEFAULT NULL, alarmStatus INT(5) DEFAULT NULL, PRIMARY KEY (id), CONSTRAINT fk_severity FOREIGN KEY (severity) REFERENCES art_log_severity_m (severityId), CONSTRAINT fk_layer FOREIGN KEY (layer) REFERENCES art_log_architecture_layer_m (layerId), CONSTRAINT fk_event_action FOREIGN KEY (eventAction) REFERENCES art_log_event_action_m (eventActionId), CONSTRAINT fk_status FOREIGN KEY (alarmStatus) REFERENCES art_log_status_m (statusId));

